# Para crear un vector en Octave, utilizamos []# Se genera vector en columna al estar con punto y comax = [1; 2; 3; 4; 5; 6; 7; 8; 9; 0]# Para calcular la longitud (la dimension), utilizamos la funcion length()len = length(x)# Sumar y restar dos o mas vectores es muy simple, siempre y cuando sean de la misma dimensiÃ³n:x = [0; 3; -1; 3; 5];y = [1; 2; 3; -1; 0];x+yx-y# El producto por un escalar tambien funciona de forma sencilla:x = [1; 0; -1; 0; 2; 0; -2];2*x-2*x5*x# Creamos una función que nos calcule el producto escalar de dos vectores# "endfor" permite cerrar el bucle y finalmente ponemos la variable a devolver# la función "fprintf" sirve para imprimir mensajes# "endif" es para acabar el "if"# "endfunction" es para acabar "function"function prodEsc  = productoEscalar(x,y)  if length(x) == length(y)    prodEsc = 0;    for i = 1:length(x)      prodEsc = prodEsc + x(i)*y(i);    endfor    prodEsc;  else    fprintf("ERROR");  endif endfunctionx = [0; 3; -1; 3; 5];y = [1; 2; 3; -1; 0];prodEsc = productoEscalar(x,y)# Para calcular la norma euclidea de un vector, lo que hacemos es utilizar la funcion norm():x = [1; 2; 0; 3; -1; 1];norma = norm(x)# Calcular Distancia Euclidea entre dos vectores function dist = distancia(x,y)  if length(x) == length(y)    dist =  norm(x-y);  else   fprintf("ERROR");  endifendfunctionx = [0; 3; -1; 3; 5];y = [1; 2; 3; -1; 0];dist = distancia(x,y)# Angulo entre dos vectores#Para radianesfunction anguloRad = angleRad(x,y)  if length(x) == length(y)    anguloRad = acos(productoEscalar(x,y)/(norm(x)*norm(y)));  else    fprintf("ERROR");  endifendfunction        x = [0; 3; -1; 3; 5];y = [1; 2; 3; -1; 0];anguloRad = angleRad(x,y)#Para gradosfunction anguloGrad = angleGrad(x,y)  if length(x) == length(y)    anguloGrad = acos(productoEscalar(x,y)/(norm(x)*norm(y)))*360/(2*pi);  else    fprintf("ERROR");  endifendfunction        x = [0; 3; -1; 3; 5];y = [1; 2; 3; -1; 0];anguloGrad = angleGrad(x,y)# Calcular la proyeccion ortogonal de v sobre ufunction proy = proyeccionOrt(u,v)  if length(u) == length(v)    proy = (productoEscalar(u,v)/norm(u).^2)*u;  else    fprintf("ERROR");  endifendfunction    u = [3; 1];v = [1; 2];proy = proyeccionOrt(u,v)# Producto vectorial function prodVec = productoVectorial(x,y)  if length(x) == length(y) && length(x) == 3    prodVec = [x(2)*y(3) - x(3)*y(2); x(1)*y(3)-x(3)*y(1); x(1)*y(2)-x(2)*y(1)];  else    fprintf("ERROR");  endifendfunctionx = [1; 2; 3];y = [0; -1; 1];prodV = productoVectorial(x,y)# Producto mixto function prodMix = productoMixto(x,y,z)  if length(x) == length(y) && length(x) == length(z) && length(z) == 3    prodMix = det([x, y, z]);  else    fprintf("ERROR");  endifendfunctionx = [1; 2; 3];y = [0; -1; 1];z = [2; 0; -3];prodM = productoMixto(x,y,z)